{"aggs":[],"params":{"spec":"{
  $schema: https://vega.github.io/schema/vega-lite/v5.json
  title: most requested area in time range
  //config: {kibana: {hideWarnings: true}}
  params: [
    {
      "name": "highlight",
      "select": {"type": "point", "on": "mouseover"}
    },
    {"name": "select", "select": "point"},
    {
      "name": "from_date",
      "value": 1,
      "bind": {"input": "datetime-local", step: 1}
    }
    {
      "name": "to_date",
      "value": 1,
      "bind": {"input": "datetime-local", "step": 1}
    }
  ]
  transform: [
    {
      "calculate": "'('+ substring(toString(datum.point.location.lat), 0, 8) + ', '+ substring(toString(datum.point.location.lon), 0, 8)+')'", 
      "as": "geopoint"
    }

    {
      "calculate": "timeParse(from_date, '%m/%d/%Y %H:%M:%S')",
      "as": "from_time"
    }
    {
      "calculate": "timeParse(to_date, '%m/%d/%Y %H:%M:%S')",
      "as": "to_time"
    }
  ]
  // Define the data source
  data: {
    url: {
      %context%: true
      index: taxi_service_index
      body: {
        "aggs":{
          "filter_date":{
            "filter": {
              "range": {
                "Date/Time": {
                  "gte": "09/01/2014 00:20:00"
                  "lte": "09/30/2014 01:20:00"
                }

              }
            },
            "aggs": {
              "geo": {
                "geohash_grid": {
                  "field": "Location",
                  "precision": 7,
                  "size": 10
                },
                "aggs": {
                  "point": {
                    "geo_centroid": {
                      "field": "Location"
                    }
                  }
                }
              }
            }
          }
        }
      }
    }

    format: {property: "aggregations.filter_date.geo.buckets"}
  }
  mark: {
    type: "bar",
    fill: "#6c8f46",
    stroke: "black",
    cursor: "pointer"
  },
  encoding: {
    x: {
      field: geopoint
      type: nominal
      axis: {title: "locations"} // Customize X axis format
    }
    y: {
      field: doc_count
      type: quantitative
      axis: {title: "request count"}
    }
    //"color": {"value": "#6c8f46"}

  "fillOpacity": {
      "condition": {"param": "select", "value": 1},
      "value": 0.3
    },
    "strokeWidth": {
      "condition": [
        {
          "param": "select",
          "empty": false,
          "value": 2
        },
        {
          "param": "highlight",
          "empty": false,
          "value": 1
        }
      ],
      "value": 0
    }
  }
}
"},"title":"most requested area in time range","type":"vega"}
